import React, { useEffect, useState } from "react";
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import drilldown from 'highcharts/modules/drilldown';
import './App.css';
import About from './About'
import { Link } from "react-router-dom";
import NavTop from "./NavTop";

drilldown(Highcharts);

function Projects() {

  const [projects, setProjects] = useState([]);


  useEffect(() => {
    const makeAPICall = async () => {
      const projectURL = "https://docs.google.com/spreadsheets/d/1ZgSrmkNKNaoR9jbnrp_he1qtHLmGJsq191HpCkT86sM/edit#gid=0";
      const resp = await fetch(`${projectURL}`);
      console.log("useEffect - resp", resp);
      let json = await res.json();
      setProjects(resp);
    };
    makeAPICall();
  }, []);
  const options = {
    chart: 
    {
      type:'pie',
      backgroundColor: '#000',

    },

      title: {
        text: 'My Projects'
      },
      xAxis: {
        type: 'category'
      },

      legend: {
        enabled: true,
      },
    series: [
      {
        name:'Software',
        colorByPoint:true,
        data:[
          {
            name: 'React',
            y:1,
            drilldown:'react',
          },
          {
            name: 'VanillaJs',
            y:1,
            drilldown:'vanillajs',
          },{
            name: 'Python',
            y:1,
            drilldown:'python',
          },

        ]
      }
    ],
    drilldown:{
      series:[
        {
          id:'react',
          data: [['Portfolio',1],['TeslaApp',1]]
        }, {
          id:'python',
          data: [['not sure yet',4],['ill add something soon',2],['something will be here soon',1] ]
        }, {
          id:'vanillajs',
          data: [['',4],['to do list',2],['Ford',1],['Chevy',1] ]
        },

      ]
    }


  };
  return (


    <div>
     <NavTop />
      <div className="project-container">
        <HighchartsReact highcharts={Highcharts} options={options}/>
      </div>


    </div>
  );
}

export default Projects;
