import React, { useContext} from "react";
import { UniversalContext } from "./App";
import NavTop from "./NavTop";
import Footer from "./Footer";
import { Link } from "react-router-dom";
import history from "./history";

function ProjectListItem(props) {
  const projectContext = useContext(UniversalContext);
  // console.log("project context", projectContext.projects);
  const data = projectContext.projects;
  console.log('props  from item',props.element)
const handleProjectClick = () => {
  projectContext.setProjectIndex(3);
  history.push('/projectdescription');
}
  return (

    <div className="allProjects" onclick={()=>handleProjectClick}>

      <div className="project-squares"> 

  <Link className ="projectsByTitle" 
  to={
    {
   pathname: `/projectdescription`,
  state: {
    from:"root"
  }

  }}
  >


    <p>{props.element.gsx$title.$t}</p>

      <p>
        <div class="flip-card">
          <div class="flip-card-inner">
            <div class="flip-card-front">
              <img alt="9" className="image" src={props.element.gsx$image.$t}></img>
            </div>
            <div class="flip-card-back">
              <img alt="10" className="image" src={props.element.gsx$image2.$t}></img>
              {props.element.gsx$description.$t}
            </div>
          </div>
        </div>
      </p>
   </Link> 

 </div>
      </div>
  );
}
export default ProjectListItem;
