import React from "react";
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import drilldown from 'highcharts/modules/drilldown';
import './App.css';

drilldown(Highcharts);

function Chart() {

  const options = {
    chart: 
    {
      type:'pie',
      backgroundColor: '#000',
    },

      title: {
        text: 'My Projects'
      },
      xAxis: {
        type: 'category'
      },

      legend: {
        enabled: false
      },
    series: [
      {
        name:'things',
        colorByPoint:true,
        data:[
          {
            name: 'Animals',
            y:5,
            drilldown:'animals',
          },
          {
            name: 'Fruits',
            y:2,
            drilldown:'fruits',
          },{
            name: 'Cars',
            y:4,
            drilldown:'cars',
          },

        ]
      }
    ],
    drilldown:{
      series:[
        {
          id:'animals',
          data: [['Cats',4],['Dogs',2],['Cows',1],['Pigs',1] ]
        }, {
          id:'fruits',
          data: [['Apples',4],['Oranges',2],['Cows',1],['Pigs',1] ]
        }, {
          id:'cars',
          data: [['Toyota',4],['Honda',2],['Ford',1],['Chevy',1] ]
        },

      ]
    }


  };
  return (
    <div>
      <div className="container">
        <HighchartsReact highcharts={Highcharts} options={options}/>
      </div>
    </div>
  );
}

export default Chart;
