import React, { useState } from "react";
import { UncontrolledCarousel } from "reactstrap";
import { Link } from "react-router-dom";
import Nav from "./Nav";
import NavTop from "./NavTop";
function About() {
  const [display, setDisplay] = useState(false);

  const handleDisplayAboutMe = () => {
    setDisplay(false);
  };
  const handleHideAboutMe = () => {
    setDisplay(true);
  };
  const items = [
    {
      src:
        "https://res.cloudinary.com/dkng1fqtz/image/upload/v1591306955/IMG_3245_k6y6zu.jpg",
      altText: "Slide 1",
      caption: "Slide 1",
      header: "Slide 1 Header",
      hieght: 2,
      key: "1",
    },
    {
      src:
        "https://res.cloudinary.com/dkng1fqtz/image/upload/v1591306946/IMG_3659_xe92gr.jpg",
      altText: "Slide 2",
      caption: "Slide 2",
      header: "Slide 2 Header",
      key: "2",
    },
    {
      src:
        "https://res.cloudinary.com/dkng1fqtz/image/upload/v1591306884/IMG_3239_rh0yqi.jpg",
      altText: "Slide 3",
      caption: "Slide 3",
      header: "Slide 3 Header",
      key: "3",
    },
  ];
  const items2 = [
    {
      src:
        "https://res.cloudinary.com/dkng1fqtz/image/upload/v1591306955/IMG_3245_k6y6zu.jpg",
      altText: "Slide 1",
      caption: "Slide 1",
      header: "Slide 1 Header",
      hieght: 2,
      key: "1",
    },
    {
      src:
        "https://res.cloudinary.com/dkng1fqtz/image/upload/v1591306946/IMG_3659_xe92gr.jpg",
      altText: "Slide 2",
      caption: "Slide 2",
      header: "Slide 2 Header",
      key: "2",
    },
    {
      src:
        "https://res.cloudinary.com/dkng1fqtz/image/upload/v1591306884/IMG_3239_rh0yqi.jpg",
      altText: "Slide 3",
      caption: "Slide 3",
      header: "Slide 3 Header",
      key: "3",
    },
  ];

  return (
    <div>
      <NavTop />
      <Link to="/">
        <p>Home</p>
      </Link>

      <div className="about">
        <div className="about-me">
          {/* <Link to="/">
            <p>Home</p>
          </Link> */}

          <UncontrolledCarousel items={items} />
        </div>
        <div className="about-me">
        <div className="footer">
            <button
              onClick={!display ? handleHideAboutMe : handleDisplayAboutMe}
            >
              {" "}
              <h1>about me</h1>{" "}
            </button>
            {display ? <UncontrolledCarousel items={items} /> : null}
          </div>
        </div>
        <div className="about-me">
          <div className="footer">
            <button
              onClick={!display ? handleHideAboutMe : handleDisplayAboutMe}
            >
              {" "}
              <h1>about me</h1>{" "}
            </button>
            {display ? <UncontrolledCarousel items={items} /> : null}
          </div>
        </div>
      </div>
      <Nav />
    </div>
  );
}

export default About;
